import React, { Component } from 'react';

export default class App extends Component {
  constructor(props) {
    super(props)
      this.state = {
        count: 0
      }
  }

  addToCount() {
    let { count } = this.state
    this.setState(count: count += 1)
  }

  render() {
    return(
      <>
        <h1>Hi</h1>
        { this.state.count }
        <button onClick={this.addToCount}>Add by 1</button>
      </>
    )
  }
}
