import React, { Component } from 'react';

export default class App extends Component {
  constructor(props) {
    super(props)
      this.state = {
        count: 0
      }
  }

  addToCount() {
    let newCount = this.state.count + 1
    this.setState({ count: newCount })
  }

  render() {
    let { count } = this.state
    return(
      <>
        <h1>Hi</h1>
        { this.state.count }
        <button onClick={ this.addToCount }>
          Add by 1
        </button>
      </>
    )
  }
}
